const emailBody = $('Extract Email Data').last().json.snippet || '';
const subject = $('Extract Email Data').last().json.subject || '';
const fullText = (subject + ' ' + emailBody).toLowerCase();

// Regex patterns for date/time formats
const patterns = [
  /\d{4}-\d{2}-\d{2}\s+\d{1,2}:\d{2}/,  // e.g. 2025-11-09 10:00
  /(january|february|march|april|may|june|july|august|september|october|november|december)\s+\d{1,2},?\s+\d{4}\s+at\s+\d{1,2}:\d{2}\s*(am|pm)?/, // November 12, 2025 at 10:00 AM
  /(jan|feb|mar|apr|may|jun|jul|aug|sep|oct|nov|dec)\.?\s+\d{1,2}\s+at\s+\d{1,2}(:\d{2})?\s*(am|pm)?/, // Nov 12 at 10am
  /(today|tomorrow|next\s+(monday|tuesday|wednesday|thursday|friday|saturday|sunday))\s+at\s+\d{1,2}(:\d{2})?\s*(am|pm)?/ // today, next Thursday etc.
];

let extractedPhrase = null;
for (const pattern of patterns) {
  const match = fullText.match(pattern);
  if (match) {
    extractedPhrase = match[0];
    break;
  }
}

// --- Helper: Convert to ISO string with Cairo timezone (+02:00)
function toCairoISO(date) {
  const offsetHours = 2; // Cairo = UTC+2
  const localTime = new Date(date.getTime() + offsetHours * 60 * 60 * 1000);
  return localTime.toISOString().replace("Z", "+02:00");
}

// --- Helper: Parse natural language like "today/tomorrow/next Thursday at 10 am"
function parseNaturalLanguage(phrase) {
  if (!phrase) return null;

  const now = new Date();
  const lower = phrase.toLowerCase();
  const days = ["sunday","monday","tuesday","wednesday","thursday","friday","saturday"];

  let targetDate = new Date(now);

  // "today"
  if (lower.includes("today")) {
    targetDate.setDate(now.getDate());
  } 
  // "tomorrow"
  else if (lower.includes("tomorrow")) {
    targetDate.setDate(now.getDate() + 1);
  } 
  // "next <weekday>"
  else {
    const match = lower.match(/next\s+(monday|tuesday|wednesday|thursday|friday|saturday|sunday)/);
    if (match) {
      const targetDay = days.indexOf(match[1]);
      const currentDay = now.getDay();
      let diff = targetDay - currentDay;
      if (diff <= 0) diff += 7;
      targetDate.setDate(now.getDate() + diff);
    }
  }

  // Extract time if available
  const timeMatch = lower.match(/(\d{1,2})(:(\d{2}))?\s*(am|pm)?/);
  if (timeMatch) {
    let hours = parseInt(timeMatch[1]);
    const minutes = timeMatch[3] ? parseInt(timeMatch[3]) : 0;
    const ampm = timeMatch[4];

    if (ampm === "pm" && hours < 12) hours += 12;
    if (ampm === "am" && hours === 12) hours = 0;

    targetDate.setHours(hours, minutes, 0, 0);
  } else {
    targetDate.setHours(10, 0, 0, 0); // default 10:00
  }

  return targetDate;
}

// --- Main extraction logic
let extractedTime = null;
if (extractedPhrase) {
  try {
    const parsed = new Date(extractedPhrase);
    if (!isNaN(parsed)) {
      extractedTime = toCairoISO(parsed);
    } else {
      const parsedNatural = parseNaturalLanguage(extractedPhrase);
      extractedTime = parsedNatural ? toCairoISO(parsedNatural) : null;
    }
  } catch {
    const parsedNatural = parseNaturalLanguage(extractedPhrase);
    extractedTime = parsedNatural ? toCairoISO(parsedNatural) : null;
  }
}

// --- Final Output
return {
  ...$input.first().json,
  extractedPhrase,
  extractedTime,
  needsManualParsing: !extractedTime
};

