// Get calendar event data from previous node
const eventData = $input.first().json;

// Helper function to format date for ICS (YYYYMMDDTHHMMSSZ)
function formatICSDate(dateString) {
  const date = new Date(dateString);
  return date.toISOString().replace(/[-:]/g, '').split('.')[0] + 'Z';
}

// Extract event details
const startTime = eventData.start?.dateTime || eventData.extractedTime;
const endTime = eventData.end?.dateTime || new Date(new Date(startTime).getTime() + 3600000).toISOString();
const summary = eventData.summary || `Meeting with ${$('Extract Email Data').first().json.sender.split('<')[0].trim()}`;
const description = eventData.description || 'Scheduled via email automation';
const location = eventData.hangoutLink || '';
const attendeeEmail = $('Extract Email Data').first().json.senderEmail;
const organizerEmail = 'alaahussienfathy@gmail.com';

// Generate ICS file content
const icsContent = `BEGIN:VCALENDAR
VERSION:2.0
PRODID:-//n8n Email Assistant//EN
CALSCALE:GREGORIAN
METHOD:REQUEST
BEGIN:VEVENT
DTSTART:${formatICSDate(startTime)}
DTEND:${formatICSDate(endTime)}
DTSTAMP:${formatICSDate(new Date().toISOString())}
UID:${eventData.id || Date.now()}@n8n-email-assistant
SUMMARY:${summary}
DESCRIPTION:${description.replace(/\n/g, '\\n')}
LOCATION:${location}
ORGANIZER;CN=Email Assistant:mailto:${organizerEmail}
ATTENDEE;CN=${attendeeEmail};RSVP=TRUE:mailto:${attendeeEmail}
STATUS:CONFIRMED
SEQUENCE:0
END:VEVENT
END:VCALENDAR`;

// Return a single item with binary property
return [
  {
    json: {}, // or include eventData if needed
    binary: {
      'invite.ics': {
        data: Buffer.from(icsContent).toString('base64'),
        mimeType: 'text/calendar',
        fileName: 'invite.ics',
      },
    },
  },
];

// Decide whether to append a new row or treat as duplicate by checking existing rows.
const sheetItems = $items("Get All Log Rows") || [];
let rows = [];
if (sheetItems && sheetItems.length) {
  rows = sheetItems.map(i => i.json);
}
const extractNode = $items("Extract Email Data") && $items("Extract Email Data")[0] ? $items("Extract Email Data")[0].json : $json;
const newMessageId = (extractNode && extractNode.messageId) ? String(extractNode.messageId).trim() : null;
if (!rows.length) {
  return [{ shouldAppend: true, isDuplicate: false, matchedRowIndex: null }];
}
let foundIndex = null;
for (let i = 0; i < rows.length; i++) {
  const r = rows[i];
  const possibleIds = [r.messageId, r['messageId'], r['Message-ID'], r['id'], r['messageId_1']];
  if (possibleIds.some(val => val && String(val).trim() === String(newMessageId).trim())) {
    foundIndex = i;
    break;
  }
}
if (foundIndex !== null) {
  return [{ shouldAppend: false, isDuplicate: true, matchedRowIndex: foundIndex }];
} else {
  return [{ shouldAppend: true, isDuplicate: false, matchedRowIndex: null }];
}

// Try to find the start time safely
const eventData = $json;

const startTime =
  eventData.start?.dateTime ||
  eventData.extractedTime ||
  eventData.start ||
  new Date().toISOString();

const timeZone =
  $('Workflow Configuration').first()?.json?.timeZone || 'Africa/Cairo';

// Format the date safely
const formattedDate = new Date(startTime).toLocaleString('en-US', {
  timeZone,
  hour12: true,
});

return {
  json: {
    ...eventData,
    formattedDate,
  },
};
